blocks.solution:
void main() {
  List<int> numbers = [5, 2, 8, 1, 9, 3];
  print(findMax(numbers)); // 9
}

int? findMax(List<int> numbers) {
  if (numbers.isEmpty) return null;
  int max = numbers[0];
  for (int num in numbers) {
    if (num > max) max = num;
  }
  return max;
}
